REPLACE INTO xcart_languages VALUES ('en','eml_abcr_abandoned_cart_notification_with_coupon_subj','Special offer for your visit at {{company}}','E-Mail');
REPLACE INTO xcart_languages VALUES ('en','eml_abcr_abandoned_cart_notification_without_coupon_subj','Humble follow-up email about your last visit at {{company}}','E-Mail');
