REPLACE INTO xcart_languages VALUES ('en','eml_abcr_hello','Hello {{name}}!','E-Mail');
REPLACE INTO xcart_languages VALUES ('en','eml_abcr_notification_message','You recently visited our site {{company_name}} ({{company_site}}), and we noticed that you still have a few items in your shopping cart. We would like to offer you a special discount towards your next purchase! Simply enter the coupon code <strong>{{coupon}}</strong> at checkout and you will {{coupon_bonus}} on your current pending order. The coupon code expires on {{expiration_time}}.\n\nIf you have any questions or require special assistance, please contact us by replying to this email. We would be happy to help you.','E-Mail');
REPLACE INTO xcart_languages VALUES ('en','eml_abcr_notification_message_discount','save <strong>{{coupon_value}}</strong>','E-Mail');
REPLACE INTO xcart_languages VALUES ('en','eml_abcr_notification_message_freeship','get <strong>free shipping</strong>','E-Mail');
REPLACE INTO xcart_languages VALUES ('en','eml_abcr_notification_without_coupon_message','You recently visited our site {{company_name}} ({{company_site}}),  and we noticed that you added some items to your shopping cart, but did not fully complete your order. We just wanted to make sure you did not encounter any problems with checkout on our site. If there is anything we can do to assist you with your online order, please do not hesitate to contact us by replying to this email.','E-Mail');
REPLACE INTO xcart_languages VALUES ('en','eml_abcr_visit_us','You can go to your shopping cart directly by clicking the special link below: {{return_link}}','E-Mail');
REPLACE INTO xcart_languages VALUES ('en','eml_abcr_visit_us_html','You can go to your shopping cart directly by clicking <a href="{{return_link}}">the special link</a>.','E-Mail');
